def getHTMLText(url):
	try:
		r=requests.get(url,headers=head,timeout=30)
		r.raise_for_status()	#如果状态码不是200，产生异常
		r.encoding='utf-8-sig'		#字符编码格式改成 utf-8
		return r.text
	except:
		#异常处理
		return "get html text error"

